      var test = this.$root.$data.votes;
      var len = test.length;
      var indices = new Array(len);

      for (var i = 0; i < len; ++i) {
        indices[i] = i;
      }

      indices.sort(function (a, b) {
        return test[a] < test[b] ? -1 : test[a] > test[b] ? 1 : 0;
      });
      return indices;
    }
